import React, { useContext, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import Acteur from '../components/Acteur';
import { BreakContexte } from '../contexte/BreakContexte';
import AlertName from '../components/AlertName';



const ActeursDetail = () => {
  const {charid} = useParams();
  console.log(charid)

  const {fetchActeur, acteur, clearActeur, nameSear, setNameSear} = useContext(BreakContexte);

  // useEffect(() => {
  //   fetchActeur(charid);
  // }, [])
  // console.log(acteur)

  useEffect(() => {
      if (charid && charid !== ''){
          fetchActeur(charid);
          setNameSear('')
      }
      return () => {
        clearActeur();
      }
  }, [])


  return (
    <div>
      <AlertName />
      <Acteur id={charid} acteur={acteur} />
    </div>
  )
}

export default ActeursDetail
