import React, { useContext, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import Acteur from '../components/Acteur';
import { BreakContexte } from '../contexte/BreakContexte';
import AlertName from '../components/AlertName';
import Spinner from '../components/Spinner';


const ActeursDetail = () => {
  const {charid} = useParams();
  // console.log(charid)

  const {loading, fetchActeur, acteur, clearActeur, nameSear, setNameSear} = useContext(BreakContexte);

  useEffect(() => {
      if (charid && charid !== ''){
          fetchActeur(charid);
      }
      return () => {
        clearActeur();
      }
  }, [])


  return (
    <>
      <div><AlertName /></div>
      {loading ? <Spinner classname={'mt-32'}/> :
      (<>
        <Acteur id={charid} acteur={acteur} />
      </>)}
    </>
  )
}

export default ActeursDetail
