import React, { createContext, useState } from "react";
import axios from "axios";
import BreakReducer from "./BreakReducer";
import { useReducer } from "react";

export const BreakContexte = createContext();

const BreakProvider = ({ children }) => {
  const [loading, setLoading] = useState(true);
  const [loadingbyname, setLoadingbyname] = useState(true);

  const initialState = {
    acteurs: [],
    acteur: {},
    acteurNom: {},
  };
  const [state, dispatch] = useReducer(BreakReducer, initialState);

  const [name, setName] = useState("");
  const [nameSear, setNameSear] = useState("");

  const fetchActeurs = async () => {
    setLoading(true);
    try {
      const response = await axios.get("https://api.github.com/users");
      console.log(response.data);
      dispatch({
        type: "GET_ACTEURS",
        payload: response.data,
      });
    } catch (error) {
      console.log(error);
    }
    setLoading(false);
  };

  const fetchActeur = async (login) => {
    setLoading(true);
    try {
      const response = await axios.get(
        `https://api.github.com/users/defunkt/${login}`
      );
      dispatch({
        type: "GET_ACTEUR",
        payload: response,
      });
      console.log(response);
    } catch (error) {
      console.log(error);
    }
    setLoading(false);
  };

  const fetchByName = async (x) => {
    setLoadingbyname(true);
    try {
      const response = await axios.get(
        `https://www.breakingbadapi.com/api/characters?name=${x}`
      );
      dispatch({
        type: "GET_ACTNAME",
        payload: response.data[0],
      });
      // console.log(response.data[0])
    } catch (error) {
      console.log(error);
    }
    setLoadingbyname(false);
  };

  const clearActeur = () => {
    dispatch({ type: "REMOVE_ACTEUR" });
  };
  const clearnomActeur = () => {
    dispatch({ type: "REMOVE_NOMACTEUR" });
  };

  return (
    <BreakContexte.Provider
      value={{
        loading,
        fetchActeurs,
        acteurs: state.acteurs,
        fetchActeur,
        acteur: state.acteur,
        clearActeur,
        acteurNom: state.acteurNom,
        fetchByName,
        name,
        setName,
        clearnomActeur,
        nameSear,
        setNameSear,
        loadingbyname,
        setLoadingbyname,
      }}
    >
      {children}
    </BreakContexte.Provider>
  );
};

export default BreakProvider;
