import React, { useContext, useEffect } from 'react';
import {Link} from 'react-router-dom';
import { BreakContexte } from '../contexte/BreakContexte';
import { useState } from 'react';



const Navbar = () => {
    const {fetchByName, acteurs, name, setName, clearnomActeur, nameSear, setNameSear, acteurNom} = useContext(BreakContexte);
    
    const change = (e) => {
        setName(e.target.value)
    }
    console.log(name)
    
    useEffect(() => {
        if (nameSear && nameSear !== ''){
            fetchByName(nameSear); 
        }
        return () => {
            clearnomActeur();
        }  
    }, [nameSear])

    console.log(nameSear)
    return (
        <div className="navbar bg-info-content px-4">
            <div className="flex-1">
                <p className="normal-case text-sm md:text-xl text-white">Breaking Bad</p>
            </div>
            <div className="flex-none gap-2">
                <div className="form-control">
                    <input 
                       type="text"
                       placeholder="Enter le nom de l'acteur"
                       value={name} 
                       onChange={change} 
                       className="input input-bordered input-sm" 
                    />
                </div>

                {acteurs.map((act) => {
                    if(name === ""){
                        setNameSear('')
                    } else if(act.name.toLowerCase().includes(name.toLowerCase())){
                        setNameSear(act.name)
                    }
                })}
                {/* <button class="btn btn-outline btn-success w-10 rounded-full">GO</button> */}
                <Link to={`/acteurs/${acteurNom.char_id}`}>
                    {/* {setNameSear('')} */}
                <button className="flex items-center justify-center px-4 border-l" >
                    <svg className="w-6 h-6 text-gray-600" fill="currentColor" xmlns="http://www.w3.org/2000/svg"
                        viewBox="0 0 24 24">
                        <path
                            d="M16.32 14.9l5.39 5.4a1 1 0 0 1-1.42 1.4l-5.38-5.38a8 8 0 1 1 1.41-1.41zM10 16a6 6 0 1 0 0-12 6 6 0 0 0 0 12z" />
                    </svg>
                </button>
                </Link>
            </div>
        </div>
    )
}

export default Navbar
