import React, { useContext, useEffect } from 'react';
import {Link} from 'react-router-dom';
import { BreakContexte } from '../contexte/BreakContexte';
import { useState } from 'react';



const Navbar = () => {
    const [name, setName] = useState('');
    const change = (e) => {
        setName(e.target.value)
    }
    console.log(name)
    const {fetchByName, acteurNom} = useContext(BreakContexte);
    console.log(acteurNom)
    useEffect(() => {
        fetchByName(name);
    }, [name])
    

    return (
        <div className="navbar bg-info-content px-4">
            <div className="flex-1">
                <p className="normal-case text-sm md:text-xl text-white">Breaking Bad</p>
            </div>
            <div className="flex-none gap-2">
                <div className="form-control">
                    <input 
                       type="text" 
                       placeholder="Rechercher" 
                       value={name} 
                       onChange={change} 
                       className="input input-bordered input-sm" 
                    />
                </div>
                <button class="btn btn-outline btn-success w-10 rounded-full">GO</button>
            </div>
        </div>
    )
}

export default Navbar
