import React from "react";
import { Link } from "react-router-dom";

const Acteur = ({ acteur, followerse, followings }) => {
  const {
    name,
    login,
    avatar_url,
    location,
    followers,
    following,
    created_at,
    twitter_username,
    email,
  } = acteur;

  return (
    <div className="h-[100vh] flex justify-center from-yellow-200 via-red-300 to-blue-300 bg-gradient-to-br shadow-2xl">
      <div class="w-full h-150px md:h-1/2 bg-white shadow-md rounded-md px-6 py-4 mt-[50px] mx-[60px] md:mx-[200px]">
        <div class="sm:flex sm:justify-between">
          <div class="flex items-center">
            <img
              class="h-12 w-12 rounded-full"
              src={avatar_url}
              alt={avatar_url}
            />
            <div class="ml-2">
              <h3 class="text-lg text-gray-800 font-medium">{name}</h3>
              <span class="text-gray-600">{email}</span>
            </div>
          </div>
          <div class="mt-2 sm:mt-0">
            <button class="flex items-center text-white bg-blue-600 rounded px-2 py-1 hover:bg-blue-500 focus:outline-none focus:shadow-outline">
              <svg
                class="h-5 w-5"
                fill="none"
                stroke-linecap="round"
                stroke-linejoin="round"
                stroke-width="2"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path d="M12 9v3m0 0v3m0-3h3m-3 0H9m12 0a9 9 0 11-18 0 9 9 0 0118 0z"></path>
              </svg>
              <span class="ml-1 text-sm">Follow</span>
            </button>
          </div>
        </div>
        <div class="flex justify-between items-center mt-4">
          <div>
            <h4 class="text-gray-600 text-sm">Login</h4>
            <span class="mt-2 text-xl font-medium text-gray-800">{login}</span>
          </div>
          <div>
            <h4 class="text-gray-600 text-sm">Submissions</h4>
            <span class="mt-2 text-xl font-medium text-gray-800">12171</span>
          </div>
          <div>
            <h4 class="text-gray-600 text-sm">Location</h4>
            <span class="mt-2 text-xl font-medium text-gray-800">
              {location}
            </span>
          </div>
        </div>
        <div class="mt-3">
          <h4 class="text-gray-600 text-sm">created_at</h4>
          <span class="mt-2 text-xl font-medium text-gray-800">
            {created_at}
          </span>
        </div>
        <div class="mt-4">
          <h4 class="text-sm text-gray-600">{followers} Followers</h4>
          <div class="flex items-center overflow-hidden mt-2">
            {followerse.map((folow) => {
              const { id, avatar_url } = folow;
              return (
                <>
                  <img
                    key={id}
                    class="-ml-2 inline-block h-6 w-6 rounded-full text-white border-2 border-white object-cover object-center"
                    src={avatar_url}
                    alt="followers"
                  />
                </>
              );
            })}
          </div>
        </div>
        <div class="mt-4">
          <h4 class="text-sm text-gray-600">{following} Following</h4>
          <div class="flex items-center overflow-hidden mt-2">
            {followings.map((folow) => {
              const { avatar_url } = folow;
              return (
                <>
                  <img
                    class="-ml-2 inline-block h-6 w-6 rounded-full text-white border-2 border-white object-cover object-center"
                    src={avatar_url}
                    alt="followings"
                  />
                </>
              );
            })}
          </div>
        </div>
        {console.log("ttttttt", followerse)}
        <a class="block mt-4 text-blue-400 hover:underline" href="#">
          3 Collections
        </a>
      </div>
    </div>
  );
};

export default Acteur;
