import React, { useContext, useEffect } from 'react';
import {Link} from 'react-router-dom';
import { BreakContexte } from '../contexte/BreakContexte';
import { useState } from 'react';



const Navbar = () => {
    const {fetchByName, acteurs, name, setName, clearnomActeur} = useContext(BreakContexte);
    
    const change = (e) => {
        setName(e.target.value)
    }
    console.log(name)
    
    // useEffect(() => {
    //     fetchByName(name);    
    // }, [name])

    return (
        <div className="navbar bg-info-content px-4">
            <div className="flex-1">
                <p className="normal-case text-sm md:text-xl text-white">Breaking Bad</p>
            </div>
            <div className="flex-none gap-2">
                <div className="form-control">
                    <input 
                       type="text"
                       placeholder="Enter le nom de l'acteur"
                       value={name} 
                       onChange={change} 
                       className="input input-bordered input-sm" 
                    />
                </div>

                {acteurs.map((act) => {
                    if(name === ""){
                        console.log('zzzzzzzzzz')
                    } else if(act.name.toLowerCase().includes(name.toLowerCase())){
                        return console.log(act.name)
                    }
                })}
                <button class="btn btn-outline btn-success w-10 rounded-full">GO</button>
            </div>
        </div>
    )
}

export default Navbar
