import React, { useContext, useEffect } from "react";
import { useParams } from "react-router-dom";
import Acteur from "../components/Acteur";
import { BreakContexte } from "../contexte/BreakContexte";
import AlertName from "../components/AlertName";
import Spinner from "../components/Spinner";

const ActeursDetail = () => {
  const { login } = useParams();
  // console.log(charid)

  const {
    loading,
    fetchActeur,
    acteur,
    fetchFollowers,
    followers,
    clearActeur,
    nameSear,
    setNameSear,
  } = useContext(BreakContexte);

  useEffect(() => {
    if (login && login !== "") {
      fetchActeur(login);
      fetchFollowers(login);
    }
    return () => {
      clearActeur();
    };
  }, [login]);

  return (
    <>
      <div>
        <AlertName />
      </div>
      {loading ? (
        <Spinner classname={"mt-32"} />
      ) : (
        <>
          <Acteur followerse={followers} login={login} acteur={acteur} />
        </>
      )}
    </>
  );
};

export default ActeursDetail;
