import React, { createContext, useState } from 'react'
import axios from 'axios'



export const BreakContexte = createContext();

const BreakProvider = ({children}) => {
  const [loading, setLoading] = useState('true')

  const fetchActeurs = async() => {
      setLoading(true);
      try {
          const response = await axios.get('https://www.breakingbadapi.com/api/characters')
          // console.log(response.data)
      } catch (error) {
          console.log(error)
      }  
      setLoading(false);
  }
  return (
    <BreakContexte.Provider value={{loading, setLoading, fetchActeurs}}>
        {children}
    </BreakContexte.Provider>
  )
}

export default BreakProvider
