import React, { useContext, useEffect } from 'react'
import { BreakContexte } from '../contexte/BreakContexte'



const Acteurs = () => {

    const {fetchActeurs, acteurs} = useContext(BreakContexte)

    useEffect(() => {
        fetchActeurs();
    }, [])


  return (
    <div className='py-10 px-6 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 text-center'>
        {acteurs.map((acteur) => {
          const {char_id, name} = acteur;
          return (
            <div key={char_id}>{name}</div>
          )
        })}
    </div>
  )
}

export default Acteurs
