import React, { useContext, useEffect, useState } from 'react'
import { BreakContexte } from '../contexte/BreakContexte'
import {Link} from 'react-router-dom';
import AlertName from '../components/AlertName';



const Home = () => {

    const {fetchActeurs, acteurs} = useContext(BreakContexte)
    useEffect(() => {
      fetchActeurs();
    }, [])

  return (
    <div className='bg-base-200 '>
    <AlertName />
    <div className="hero min-h-screen pb-32">
      <div className="hero-content flex-grow-0 flex-col px-6 sm:px-20">
        <div className="carousel rounded-box">
          {acteurs.map((acteur) => (
            <div key={acteur.char_id} className="carousel-item w-full">
              <img src={acteur.img} className="h-80" alt="Tailwind CSS Carousel component" />
            </div>
          ))}  
        </div>
        <div className=''>
          <h1 className="text-5xl font-bold">breaking bad serie</h1>
          <p className="py-6">
              Breaking Bad, ou Breaking Bad : Le Chimiste1 au Québec, est une série télévisée 
              américaine en 62 épisodes de 47 minutes, créée par Vince Gilligan, 
              diffusée simultanément du 20 janvier 2008 au 29 septembre 2013 sur AMC aux États-Unis 
              et au Canada, et ensuite sur Netflix.
          </p>
          <Link to='/acteurs' className="btn btn-primary">
            Les Acteurs
          </Link>
        </div>
      </div>
    </div>
</div>
  )
}

export default Home
