import React, { useContext, useEffect } from 'react'
import { BreakContexte } from '../contexte/BreakContexte'
import { Link } from 'react-router-dom';


const Acteurs = () => {

    const {fetchActeurs, acteurs, name, acteurNom} = useContext(BreakContexte)


    console.log(acteurNom)
    useEffect(() => {
        fetchActeurs();
    }, [])


  return (
    <div className='flex flex-col'>
    {(name !== '') && (
        <div className="alert text-white shadow-lg w-1/2 m-auto">
          <div>
            <svg xmlns="http://www.w3.org/2000/svg" class="stroke-current flex-shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" /></svg>
            <span>{`Peut etre vous chercher: ${acteurNom.name} !`}</span>
          </div>
        </div>)}
    <div className='pt-10 pb-32 px-6 grid grid-cols-1 gap-8 sm:grid-cols-2 lg:grid-cols-3 text-center'>
        {acteurs.map((acteur) => {
          const {char_id, name, img} = acteur;
          return (
            <div key={char_id} className='card card-side px-2 bg-secondary-content shadow-2xl'>
              <div className="avatar my-auto">
                  <div className='mask mask-squircle w-36 h-36'>
                    <img className='object-top' src={img} alt="image" />
                  </div> 
              </div>  
              <div className='card-body'>
                <h2 class="card-title m-auto">{name}</h2>
                <div class="card-actions m-auto"> 
                    <Link to={`/acteurs/${char_id}`}>
                      <button class="btn btn-outline btn-primary">
                        Plus
                      </button>
                    </Link> 
                </div>
              </div>
             </div>)
        })}
    </div>
    </div>
  )
}

export default Acteurs
