import React from "react";
import { Link } from "react-router-dom";

const Acteur = ({ acteur }) => {
  const { login, avatar_url, location, followers } = acteur;
  // console.log(acteur)
  return (
    <div className="card sm:card-side bg-base-100 shadow-2xl w-3/4 mx-auto mt-8 mb-44 pt-10">
      <div className="avatar mx-auto md:mt-2 mb-2 md:ml-12">
        <div className="w-44 h-56 sm:w-64 sm:h-64 rounded">
          <img className="object-top rounded-xl" src={avatar_url} alt="image" />
        </div>
      </div>
      <div className="card-body p-1">
        <div className="stats stats-vertical shadow bg-primary-content w-3/4 m-auto border-black">
          <div className="stat border-black">
            {/* <div className="stat-title">Nom:</div> */}
            <div className="stat-value text-sm xl:text-xl">Nom: {login}</div>
            <div className="stat-value text-sm xl:text-xl">
              followers: {followers}
            </div>
            <div className="stat-desc">Adresse: {location}</div>
          </div>
          <div className="stat border-black">
            <div className="stat-title">categorie:</div>
            <div className="stat-value text-sm xl:text-xl">{login}</div>
            <div className="stat-desc">portrayed: {login}</div>
          </div>
          <div className="stat border-black">
            <div className="stat-title">Autres:</div>
            {/* <div className="stat-value">1,200</div> */}
            {/* <div className="stat-desc">
              Date de naissance:{" "}
              {birthday === "Unknown" ? "Pas de date" : birthday}
            </div> */}
            {/* <div className="stat-desc">status: {status}</div> */}
          </div>
        </div>
        <div className="card-actions justify-end m-2">
          <Link to={"/acteurs"}>
            <button className="btn btn-primary">Retour</button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Acteur;
