import React, { useContext, useEffect } from 'react'
import { BreakContexte } from '../contexte/BreakContexte'
import { useNavigate, useParams } from 'react-router-dom'


const Acteurs = () => {

    const navigate = useNavigate();
    const {fetchActeurs, acteurs} = useContext(BreakContexte)
    const {charid} = useParams();
    console.log(charid);
    const handleClick = (id) => {
      navigate(`/${id}`);
    }

    useEffect(() => {
        fetchActeurs();
    }, [])


  return (
    <div className='py-10 px-6 bg-accent-content grid grid-cols-1 gap-8 xl:grid-cols-4 lg:grid-cols-3 md:grid-cols-2 text-center'>
        {/* {acteurs.map((acteur) => {
          const {char_id, name} = acteur;
          return (
            <div key={char_id}>

            </div>
          )
        })} */}
        {acteurs.map((acteur) => {
          const {char_id, name, img} = acteur;
          return (
            <div key={char_id} className='card card-side px-2 bg-secondary-content shadow-xl'>
              <div className="avatar my-auto">
                  <div className='mask mask-squircle w-36 h-36'>
                    <img className='object-top' src={img} alt="image" />
                  </div> 
              </div>  
              <div className='card-body'>
                <h2 class="card-title">{name}</h2>
                <div class="card-actions m-auto">
                  <button class="btn btn-primary" onClick={handleClick(char_id)}>
                    Plus
                  </button>
                </div>
              </div>
             </div>)
        })}
    </div>
  )
}

export default Acteurs
