import React, { useContext, useEffect } from "react";
import { useParams } from "react-router-dom";
import Acteur from "../components/Acteur";
import { BreakContexte } from "../contexte/BreakContexte";
import AlertName from "../components/AlertName";
import Spinner from "../components/Spinner";
import { useState } from "react";

const ActeursDetail = () => {
  const { login } = useParams();
  const [actr, setActr] = useState([]);
  const [actrFoll, setActrFoll] = useState([]);
  // console.log(charid)

  const {
    loading,
    fetchActeur,
    acteur,
    fetchFollowers,
    followers,
    clearActeur,
    nameSear,
    setNameSear,
  } = useContext(BreakContexte);

  // useEffect(() => {
  //   if (login !== "") {
  //     fetchActeur(login);
  //     fetchFollowers(login);
  //   }
  //   return () => {
  //     clearActeur();
  //   };
  // }, []);

  useEffect(() => {
    Promise.all([fetchActeur(login), fetchFollowers(login)])
      .then(([resActr, resActrFoll]) =>
        Promise.all([resActr.json(), resActrFoll.json()])
      )
      .then(([dataActr, dataActrFoll]) => {
        setActr(dataActr);
        setActrFoll(dataActrFoll);
      });
  }, []);

  console.log("Actr", actr);

  return (
    <>
      <div>
        <AlertName />
      </div>
      {loading ? (
        <Spinner classname={"mt-32"} />
      ) : (
        <>
          <Acteur followerse={followers} login={login} acteur={acteur} />
        </>
      )}
    </>
  );
};

export default ActeursDetail;
