import React, { createContext, useState } from 'react'
import axios from 'axios'
import BreakReducer from './BreakReducer'



export const BreakContexte = createContext();

const BreakProvider = ({children}) => {
  const [loading, setLoading] = useState('true')

  const initialState = {
    acteurs: [],
  }
  const [state, dispatch] = useReducer(BreakReducer, initialState);


  const fetchActeurs = async() => {
      setLoading(true);
      try {
          const response = await axios.get('https://www.breakingbadapi.com/api/characters')
          console.log(response.data)
          dispatch({
            type: 'GET_ACTEURS',
            payload: response.data,
          })
      } catch (error) {
          console.log(error)
      }  
      setLoading(false);
      
  }
  return (
    <BreakContexte.Provider value={{loading, setLoading, fetchActeurs, getActeurs}}>
        {children}
    </BreakContexte.Provider>
  )
}

export default BreakProvider
