    const {fetchByName, acteurs, name1, setName1, clearnomActeur} = useContext(BreakContexte);
    
    const change = (e) => {
        setName1(e.target.value)
    }
    console.log(name1)
    
    // useEffect(() => {
    //     fetchByName(name);    
    // }, [name])

    return (
        <div className="navbar bg-info-content px-4">
            <div className="flex-1">
                <p className="normal-case text-sm md:text-xl text-white">Breaking Bad</p>
            </div>
            <div className="flex-none gap-2">
                <div className="form-control">
                    <input 
                       type="text"
                       placeholder="Enter le nom de l'acteur"
                       value={name1} 
                       onChange={change} 
                       className="input input-bordered input-sm" 
                    />
                </div>

                {acteurs.map((act) => {
                    if(name1 === ""){
                        console.log('zzzzzzzzzz')
                    } else if(act.name.tolowerCase().includes(name1.tolowerCase())){
                        return (<div>{act.name}</div>)
                    }
                })}
                <button class="btn btn-outline btn-success w-10 rounded-full">GO</button>
            </div>
        </div>
    )
}

export default Navbar
