import React, { createContext, useState } from 'react'
import axios from 'axios'
import BreakReducer from './BreakReducer'
import { useReducer } from 'react';



export const BreakContexte = createContext();

const BreakProvider = ({children}) => {
  const [loading, setLoading] = useState('true')

  const initialState = {
    acteurs: [],
    acteur: {},
    acteurNom: {},
  }
  const [state, dispatch] = useReducer(BreakReducer, initialState);

  const [name, setName] = useState('');

  const fetchActeurs = async() => {
      setLoading(true);
      try {
          const response = await axios.get('https://www.breakingbadapi.com/api/characters')
          // console.table(response.data)
          dispatch({
            type: 'GET_ACTEURS',
            payload: response.data,
          })
      } catch (error) {
          console.log(error)
      }  
      setLoading(false);    
  }

  const fetchActeur = async(id) => {
    setLoading(true);
    try {
      const response = await axios.get(`https://www.breakingbadapi.com/api/characters/${id}`);
      dispatch({
        type: 'GET_ACTEUR',
        payload: response.data[0],
      })
      // console.log(response.data[0])
    } catch (error) {
      console.log(error);
    }
  }

  const fetchByName = async(name) => {
    setLoading(true);
    try {
      const response = await axios.get(`https://www.breakingbadapi.com/api/characters?name=${name}`);
      dispatch({
        type: 'GET_ACTNAME',
        payload: response.data[0],
      })
      // console.log(response.data[0])
    } catch (error) {
      console.log(error);
    }
  }

  const clearActeur = () => {
    dispatch({type: 'REMOVE_ACTEUR'})
  }

  return (
    <BreakContexte.Provider value={
      {
        fetchActeurs,
        acteurs: state.acteurs,
        fetchActeur,
        acteur: state.acteur,
        clearActeur,
        acteurNom: state.acteurNom,
        fetchByName,
        name,
      }
    }>
        {children}
    </BreakContexte.Provider>
  )
}

export default BreakProvider
