import React, { useContext, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import Acteur from '../components/Acteur';
import { BreakContexte } from '../contexte/BreakContexte';




const ActeursDetail = () => {
  const {charid} = useParams();
  console.log(charid)

  const {acteur} = useContext(BreakContexte);

  const fetchActeur = async(id) => {
    setLoading(true);
    try {
      const response = await axios.get(`https://www.breakingbadapi.com/api/characters/${id}`);
      dispatch({
        type: 'GET_ACTEUR',
        payload: response.data,
      })
      console.log(response.data)
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    fetchActeur(charid);
  }, [])
  console.log(acteur)


  return (
    <div>
      <Acteur id={charid} acteur={acteur} />
    </div>
  )
}

export default ActeursDetail
