import React, { useContext, useEffect } from 'react'
import { BreakContexte } from '../contexte/BreakContexte'
import { Link } from 'react-router-dom';


const Acteurs = () => {
    const {fetchActeurs, acteurs} = useContext(BreakContexte)
    useEffect(() => {
        fetchActeurs();
    }, [])


  return (    
    <div className='pt-10 pb-32 px-6 grid grid-cols-1 gap-8 sm:grid-cols-2 lg:grid-cols-3 text-center'>
        {acteurs.map((acteur) => {
          const {char_id, name, img} = acteur;
          return (
            <div key={char_id} className='card card-side px-2 bg-secondary-content shadow-2xl'>
              <div className="avatar my-auto">
                  <div className='mask mask-squircle w-36 h-36'>
                    <img className='object-top' src={img} alt="image" />
                  </div> 
              </div>  
              <div className='card-body'>
                <h2 class="card-title m-auto">{name}</h2>
                <div class="card-actions m-auto"> 
                    <Link to={`/acteurs/${char_id}`}>
                      <button class="btn btn-outline btn-primary">
                        Plus
                      </button>
                    </Link> 
                </div>
              </div>
             </div>)
        })}
    </div>
  )
}

export default Acteurs
