import React, { createContext, useState } from "react";
import axios from "axios";
import BreakReducer from "./BreakReducer";
import { useReducer } from "react";

export const BreakContexte = createContext();

const BreakProvider = ({ children }) => {
  const [loading, setLoading] = useState(true);
  const [loadingbyname, setLoadingbyname] = useState(true);

  const initialState = {
    acteurs: [],
    acteur: {},
    acteurNom: {},
  };
  const [state, dispatch] = useReducer(BreakReducer, initialState);

  const [name, setName] = useState("");
  const [nameSear, setNameSear] = useState("");

  const fetchActeurs = () => {
    setLoading(true);
    const options = {
      method: "GET",
      url: "https://movies-details1.p.rapidapi.com/title/find",
      params: { q: "game of thr" },
      headers: {
        "X-RapidAPI-Key": "d6b89bc6a2msh1c3504d409c851cp1470e5jsn97545f8772de",
        "X-RapidAPI-Host": "movies-details1.p.rapidapi.com",
      },
    };
    axios
      .request(options)
      .then(function (response) {
        setData(response.data.results);
        console.log(response.data.results);
        dispatch({
          type: "GET_ACTEURS",
          payload: response.data,
        });
      })
      .catch(function (error) {
        console.error(error);
      });
    setLoading(false);

    // try {
    //     const response = await axios.get('https://www.breakingbadapi.com/api/characters')
    //     // console.table(response.data)
    //     dispatch({
    //       type: 'GET_ACTEURS',
    //       payload: response.data,
    //     })
    // } catch (error) {
    //     console.log(error)
    // }
    // setLoading(false);
  };

  const fetchActeur = async (id) => {
    setLoading(true);
    try {
      const response = await axios.get(
        `https://www.breakingbadapi.com/api/characters/${id}`
      );
      dispatch({
        type: "GET_ACTEUR",
        payload: response.data[0],
      });
      // console.log(response.data[0])
    } catch (error) {
      console.log(error);
    }
    setLoading(false);
  };

  const fetchByName = async (x) => {
    setLoadingbyname(true);
    try {
      const response = await axios.get(
        `https://www.breakingbadapi.com/api/characters?name=${x}`
      );
      dispatch({
        type: "GET_ACTNAME",
        payload: response.data[0],
      });
      // console.log(response.data[0])
    } catch (error) {
      console.log(error);
    }
    setLoadingbyname(false);
  };

  const clearActeur = () => {
    dispatch({ type: "REMOVE_ACTEUR" });
  };
  const clearnomActeur = () => {
    dispatch({ type: "REMOVE_NOMACTEUR" });
  };

  return (
    <BreakContexte.Provider
      value={{
        loading,
        fetchActeurs,
        acteurs: state.acteurs,
        fetchActeur,
        acteur: state.acteur,
        clearActeur,
        acteurNom: state.acteurNom,
        fetchByName,
        name,
        setName,
        clearnomActeur,
        nameSear,
        setNameSear,
        loadingbyname,
        setLoadingbyname,
      }}
    >
      {children}
    </BreakContexte.Provider>
  );
};

export default BreakProvider;
